<script type="text/javascript">
var areAllItemsCheckedOnce = false;
var ARIS = {};

function itemObj(itemId){
	this.itemId = itemId;
	this.qty = 0;
	this.wasChecked = false;
}

function removeOneItemAtRandom(){
	var tempItemArray = [];
	//First set them all to not checked
	for (i = 0; i < itemArray.length; i++) {
		var itemObject = itemArray[i];
		itemObject.wasChecked = false;
		//If the item's quantity is greater then 0 (meaning the user has picked up at least one of said item)
		if(itemObject.qty > 0){
			//Add that item to the temp array.
			tempItemArray.push(itemObject);
		}
	}
	//Get a random object from the temp array
	var randNum = Math.floor(Math.random() * tempItemArray.length);
//	alert("random number = " + randNum);
	var randomObj = tempItemArray[randNum];
	areAllItemsCheckedOnce = true;
	//Remove one item from the random object quantity.
	ARIS.takeItemCount(randomObj.itemId, 1);

}

