var itemArray = [cellPhone, laptop, water, gloves, solarCellphoneCharger, graphicNovel, photos, clothes, toothbrush, firstAidKit, flashlight, novelWithPicture, food, fireStarter, matches, knife, batteries, hazelnuts, harmonica, berries, oysterMushrooms, tamborine, firewood, guitar, rifle];

ARIS.ready = function() {

	for (i = 0; i < itemArray.length; i++) { 
		var itemObject = itemArray[i];
		ARIS.getItemCount(itemObject.itemId);
	}

	ARIS.didUpdateItemQty = function(item_id, qty) {
		if(areAllItemsCheckedOnce != true){
			for (i = 0; i < itemArray.length; i++) {
				var item = itemArray[i];
				if(item_id == item.itemId){
					item.qty = qty;
					item.wasChecked = true;
					break; // break out of the loop since we've set the item that was currently being updated
				}
			}

			//Loop through the array and check if they are all TRUE for wasChecked
			//If there is even one that is FALSE, then exit the loop
			var allItemsChecked = true;
			for (i = 0; i < itemArray.length; i++) {
				var itemObject = itemArray[i];
				if(itemObject.wasChecked == false){
					allItemsChecked = false;
					break;
				}
				if(i == itemArray.length-1){
					allItemsChecked = true;
				}
			}
//			alert("all items checked = " + allItemsChecked);
			if(allItemsChecked == true){
//				alert("all items checked = " + allItemsChecked);
				areAllItemsCheckedOnce = true;
//				alert("ALL HAVE BEEN CHECKED!" + firstAidKit.qty + " " + water.qty + " " + gloves.qty + " " + food.qty + " " + solarCellphoneCharger.qty + " " + graphicNovel.qty + " ");
				//code before the pause
				setTimeout(function(){
				    //do what you need here
					removeOneItemAtRandom();
				}, 2000);
			}
		}
	}

}
</script>

